func TestTCP_Forward_Direct(t *testing.T) {
	for _, compress := range []bool{true, false} {
		func() {
			// server
			ln, err := Listen("tcp", "127.0.0.1:0", AdornSnappy(compress))
			require.NoError(t, err)

			go func() {
				for {
					conn, err := ln.Accept()
					if err != nil {
						return
					}
					go func() {
						buf := make([]byte, 20)
						n, err := conn.Read(buf)
						if !assert.NoError(t, err) {
							return
						}
						assert.Equal(t, "ping", string(buf[:n]))
						_, err = conn.Write([]byte("pong"))
						if !assert.NoError(t, err) {
							return
						}
					}()
				}
			}()
			defer ln.Close()

			// client
			d := &Client{
				Timeout:     time.Second,
				AdornChains: AdornConnsChain{AdornSnappy(compress)},
			}
			cli, err := d.Dial("tcp", ln.Addr().String())
			require.NoError(t, err)
			defer cli.Close()

			_, err = cli.Write([]byte("ping"))
			require.NoError(t, err)
			b := make([]byte, 20)
			n, err := cli.Read(b)
			require.NoError(t, err)
			require.Equal(t, "pong", string(b[:n]))
		}()
	}
}

var base64CaCrt = `base64:`
var base64Key = `base64:`
var base64Crt = `base64:`

// TLSConfig tcp tls config
// Single == true,  单向认证
//      客户端必须有提供ca证书
//      服务端必须有私钥和由ca签发的证书
// Single == false  双向认证
//      客户端必须有私钥和由ca签发的证书,ca证书可选(无将使用由ca签发的证书)
//      服务端必须有私钥和由ca签发的证书,ca证书可选(无将使用由ca签发的证书)
type TLSConfig struct {
	CaCert []byte
	Cert   []byte
	Key    []byte
	Single bool
}

// ClientConfig client tls config
func (sf *TLSConfig) ClientConfig() (*tls.Config, error) {
	if sf.Single {
		if len(sf.CaCert) == 0 {
			return nil, errors.New("invalid root certificate")
		}

		certPool := x509.NewCertPool()
		ok := certPool.AppendCertsFromPEM(sf.CaCert)
		if !ok {
			return nil, errors.New("failed to parse root certificate")
		}
		return &tls.Config{
			RootCAs:            certPool,
			InsecureSkipVerify: true,
			VerifyPeerCertificate: func(rawCerts [][]byte, verifiedChains [][]*x509.Certificate) error {
				opts := x509.VerifyOptions{Roots: certPool}
				for _, rawCert := range rawCerts {
					cert, _ := x509.ParseCertificate(rawCert)
					_, err := cert.Verify(opts)
					if err != nil {
						return err
					}
				}
				return nil
			},
		}, nil
	}

	certificate, err := tls.X509KeyPair(sf.Cert, sf.Key)
	if err != nil {
		return nil, err
	}

	certPool := x509.NewCertPool()
	caBytes := sf.Cert
	if sf.CaCert != nil {
		caBytes = sf.CaCert
	}
	ok := certPool.AppendCertsFromPEM(caBytes)
	if !ok {
		return nil, errors.New("failed to parse root certificate")
	}
	block, _ := pem.Decode(caBytes)
	if block == nil {
		return nil, errors.New("failed to parse certificate PEM")
	}
	x509Cert, err := x509.ParseCertificate(block.Bytes)
	if err != nil || x509Cert == nil {
		return nil, errors.New("failed to parse block")
	}

	return &tls.Config{
		RootCAs:            certPool,
		Certificates:       []tls.Certificate{certificate},
		InsecureSkipVerify: true,
		ServerName:         x509Cert.Subject.CommonName,
		VerifyPeerCertificate: func(rawCerts [][]byte, verifiedChains [][]*x509.Certificate) error {
			opts := x509.VerifyOptions{Roots: certPool}
			for _, rawCert := range rawCerts {
				cert, _ := x509.ParseCertificate(rawCert)
				_, err := cert.Verify(opts)
				if err != nil {
					return err
				}
			}
			return nil
		},
	}, nil
}

// ServerConfig server tls config
func (sf *TLSConfig) ServerConfig() (*tls.Config, error) {
	certificate, err := tls.X509KeyPair(sf.Cert, sf.Key)
	if err != nil {
		return nil, err
	}
	config := &tls.Config{Certificates: []tls.Certificate{certificate}}
	if !sf.Single {
		certPool := x509.NewCertPool()
		caBytes := sf.Cert
		if sf.CaCert != nil {
			caBytes = sf.CaCert
		}
		ok := certPool.AppendCertsFromPEM(caBytes)
		if !ok {
			return nil, errors.New("parse root certificate")
		}
		config.ClientCAs = certPool
		config.ClientAuth = tls.RequireAndVerifyClientCert
	}
	return config, nil
}

func TestTcpTls_Forward_Direct(t *testing.T) {
	caCrt, err := extcert.LoadCrt(base64CaCrt)
	require.NoError(t, err)
	crt, key, err := extcert.LoadPair(base64Crt, base64Key)
	require.NoError(t, err)

	for _, compress := range []bool{true, false} {
		for _, single := range []bool{true, false} {
			func() {
				srvCfg := TLSConfig{
					CaCert: caCrt,
					Cert:   crt,
					Key:    key,
					Single: single,
				}

				serverConfig, err := srvCfg.ServerConfig()
				require.NoError(t, err)

				// server
				ln, err := Listen("tcp", "127.0.0.1:0", BaseAdornTLSServer(serverConfig), AdornSnappy(compress))
				require.NoError(t, err)
				defer ln.Close()

				go func() {
					for {
						conn, err := ln.Accept()
						if err != nil {
							return
						}
						go func() {
							buf := make([]byte, 20)
							n, err := conn.Read(buf)
							if !assert.NoError(t, err) {
								return
							}
							assert.Equal(t, "ping", string(buf[:n]))
							_, err = conn.Write([]byte("pong"))
							if !assert.NoError(t, err) {
								return
							}
						}()
					}
				}()

				// client
				cliConfig := TLSConfig{
					CaCert: caCrt,
					Cert:   crt,
					Key:    key,
					Single: single,
				}
				clientConfig, err := cliConfig.ClientConfig()
				require.NoError(t, err)

				d := &Client{
					Timeout:     time.Second,
					AdornChains: AdornConnsChain{BaseAdornTLSClient(clientConfig), AdornSnappy(compress)},
				}

				cli, err := d.Dial("tcp", ln.Addr().String())
				require.NoError(t, err)
				defer cli.Close()

				_, err = cli.Write([]byte("ping"))
				require.NoError(t, err)
				b := make([]byte, 20)
				n, err := cli.Read(b)
				require.NoError(t, err)
				require.Equal(t, "pong", string(b[:n]))
			}()
		}
	}
}

