func TestSSSSTCP(t *testing.T) {
	password := "pass_word"
	method := "aes-192-cfb"
	compress := false
	want := []byte("1flkdfladnfadkfna;kdnga;kdnva;ldk;adkfpiehrqeiphr23r[ingkdnv;ifefqiefn")

	// server
	ln, err := Listen("tcp", "127.0.0.1:0", BaseAdornStcp(method, password), AdornSnappy(compress))
	require.NoError(t, err)
	defer ln.Close()
	go func() {
		for {
			conn, err := ln.Accept()
			if err != nil {
				return
			}
			go func() {
				buf := make([]byte, 512)
				n, err := conn.Read(buf)
				if !assert.NoError(t, err) {
					return
				}
				assert.Equal(t, want, buf[:n])
				_, err = conn.Write(want)
				if !assert.NoError(t, err) {
					return
				}
			}()
		}
	}()

	// client twice
	for i := 0; i < 2; i++ {
		func() {
			d := Client{
				Timeout:     time.Second,
				AdornChains: AdornConnsChain{BaseAdornStcp(method, password), AdornSnappy(compress)},
			}
			cli, err := d.Dial("tcp", ln.Addr().String())
			require.NoError(t, err)
			defer cli.Close()

			_, err = cli.Write(want)
			require.NoError(t, err)

			got := make([]byte, 512)
			n, err := cli.Read(got)
			require.NoError(t, err)
			require.Equal(t, want, got[:n])
		}()
	}
}

func TestTCP_Forward(t *testing.T) {
	for _, compress := range []bool{true, false} {
		func() {
			// server
			ln, err := Listen("tcp", "127.0.0.1:0", AdornSnappy(compress))
			require.NoError(t, err)

			go func() {
				for {
					conn, err := ln.Accept()
					if err != nil {
						return
					}
					go func() {
						buf := make([]byte, 20)
						n, err := conn.Read(buf)
						if !assert.NoError(t, err) {
							return
						}
						assert.Equal(t, "ping", string(buf[:n]))
						_, err = conn.Write([]byte("pong"))
						if !assert.NoError(t, err) {
							return
						}
					}()
				}
			}()
			defer ln.Close()

			// client
			d := &Client{
				Timeout:     time.Second,
				AdornChains: AdornConnsChain{AdornSnappy(compress)},
				Forward:     &net.Dialer{Timeout: time.Second},
			}
			cli, err := d.Dial("tcp", ln.Addr().String())
			require.NoError(t, err)
			defer cli.Close()

			_, err = cli.Write([]byte("ping"))
			require.NoError(t, err)
			b := make([]byte, 20)
			n, err := cli.Read(b)
			require.NoError(t, err)
			require.Equal(t, "pong", string(b[:n]))
		}()
	}
}
